#!/usr/bin/env python3

dna = ''

a_cnt = dna.count('A') + dna.count('a')

c_cnt = dna.count('C') + dna.count('c')

g_cnt = dna.count('G') + dna.count('g')

t_cnt = dna.count('T') + dna.count('t')

print(f'a={a_cnt}, c={c_cnt}, g={g_cnt}, t={t_cnt}')

print('converting...')

dna = dna.replace('T', 'U')

dna = dna.replace('t', 'u')

a_cnt = dna.count('A') + dna.count('a')

c_cnt = dna.count('C') + dna.count('c')

g_cnt = dna.count('G') + dna.count('g')

t_cnt = dna.count('T') + dna.count('t')

print(f'a={a_cnt}, c={c_cnt}, g={g_cnt}, t={t_cnt}')
