#!/usr/bin/env python3

DNA = ''

DNA_count = len(DNA)

DNA_a_count = DNA.count('A')

DNA_c_count = DNA.count('C')

DNA_t_count = DNA.count('T')

DNA_g_count = DNA.count('G')

print(f'''this sequence contains {DNA_count} nucleotides, 
{DNA_a_count} of which are adenosines,
{DNA_c_count} of which are cytosines,
{DNA_t_count} of which are thymines, 
{DNA_g_count} of which are guanine''')
